update TIM0211
   set
	 STATUS     = #{status}
	,MOD_DATE   = #{getDate}
	,MOD_USER   = #{regApp}
 where AGENT_ID = #{agentId}

update TIM0211
   set
	 STATUS     = #{status}
	,MOD_DATE   = #{getDate}
where STATUS !='0'

insert into TOP0810(
	 AGENT_ID
	,GET_DATE
	,SEQ
	,STATUS
	,MSG
	,REG_DATE
	,REG_APP
) values (
	 #{agentId}
	,#{getDate}
	,(select coalesce(max(SEQ),0) + 1 from TOP0810 where AGENT_ID = #{agentId} and GET_DATE = #{getDate})
	,#{status}
	,#{msg}
	,#{regDate}
	,#{regApp}
)

mapper.update:
		update TOP0810
           set
			 GET_DATE = #{getDate,jdbcType=VARCHAR}
			,STATUS   = #{status,jdbcType=VARCHAR}
			,MSG      = #{msg,jdbcType=VARCHAR}
			,REG_DATE = #{regDate,jdbcType=VARCHAR}
			,REG_APP  = #{regApp,jdbcType=VARCHAR}
		 where AGENT_ID = #{agentId}
		   and GET_DATE = (SELECT max(GET_DATE) FROM TOP0810 WHERE AGENT_ID  = #{agentId})

mapper.select:
select a.AGENT_ID as "agentId" ,a.GET_DATE as "getDate" ,a.SEQ as "seq" ,a.STATUS as "status" ,a.MSG as "msg" ,a.REG_DATE as "regDate" ,a.REG_APP as "regApp" from TOP0810 a inner join TIM0211 b on a.AGENT_ID = b.AGENT_ID and a.GET_DATE >= #{fromDate} and a.GET_DATE <= #{toDate}
and b.AGENT_NM LIKE #{agentNm}
and a.STATUS = #{status}
order by a.AGENT_ID, a.GET_DATE desc, a.SEQ desc
