mapper.select:
SELECT COUNT(QA_MASTERLOG.MSG_DATETIME) AS TOTAL_CNT FROM ( SELECT QA_MASTERLOG.MSG_DATETIME, QA_MASTERLOG.MASTERLOG_ID FROM QA_MASTERLOG QA_MASTERLOG WHERE 1 = 1 AND MSG_DATETIME BETWEEN #{startDate} AND #{endDate}
AND MSG_STATUS = #{status}
AND MSG_DATETIME = #{msgDateTime}
) QA_MASTERLOG
INNER JOIN ( SELECT DISTINCT ID_MAP.MASTERLOG_ID , ID_MAP.GROUP_ID , ID_MAP.INTF_ID
, NVL(TAN0201.INTEGRATION_ID, ID_MAP.INTF_ID) AS INTEGRATION_ID , NVL(TAN0201.INTERFACE_ID , ID_MAP.INTF_ID) AS INTERFACE_ID FROM QA_MASTERLOG_ID_MAP ID_MAP
LEFT OUTER JOIN
INNER JOIN
( SELECT DISTINCT TAN0201.INTEGRATION_ID , TAN0201.INTERFACE_ID , TAN0201.INTERFACE_NM , TAN0201.CHANNEL_ID , TAN0101.BUSINESS_ID FROM TAN0201 TAN0201 INNER JOIN TAN0101 ON TAN0101.INTERFACE_ID = TAN0201.INTERFACE_ID
INNER JOIN TAN0213 ON TAN0213.INTERFACE_ID = TAN0201.INTERFACE_ID INNER JOIN TIM0102 ON TAN0213.SYSTEM_ID = TIM0102.C_SYS_ID AND TIM0102.P_SYS_ID = #{systemId}
INNER JOIN TAN0213 ON TAN0213.INTERFACE_ID = TAN0201.INTERFACE_ID INNER JOIN TIM0403 ON TIM0403.SYSTEM_ID = TAN0213.SYSTEM_ID INNER JOIN TIM0402 ON TIM0402.C_ORG_ID = TIM0403.ORG_ID AND TIM0402.P_ORG_ID = #{organizationId} INNER JOIN TIM0102 ON TIM0102.P_SYS_ID = TIM0403.SYSTEM_ID
----------------------------------------------- -- 데이터 엑세스 권한 별 인터페이스 조회 ----------------------------------------------- INNER JOIN ( SELECT DISTINCT TSU0220.INTERFACE_ID FROM ----------------------------------------------- -- 로그인 사용자 데이터 엑세스 권한 조회(공통) ----------------------------------------------- ( SELECT DISTINCT TSU0219.USER_ID AS USER_ID, TSU0221.C_ROLE_ID AS ROLE_ID FROM TSU0219 INNER JOIN TSU0221 ON TSU0221.P_ROLE_ID = TSU0219.ROLE_ID WHERE TSU0219.USER_ID = #{userId} ) ACCESS_ROLE ----------------------------------------------- -- 데이터 엑세스 권한 :: 인터페이스 맵핑 조인 ----------------------------------------------- INNER JOIN TSU0220 TSU0220 ON TSU0220.ROLE_ID = ACCESS_ROLE.ROLE_ID
UNION
----------------------------------------------- -- 로그인 사용자 본인이 등록한 인터페이스 목록 조회 ----------------------------------------------- SELECT INTERFACE_ID FROM TAN0201 WHERE REG_USER = #{userId} ) ACCESS_ROLE_INTF ON TAN0201.INTERFACE_ID = ACCESS_ROLE_INTF.INTERFACE_ID
INNER JOIN TSU0220 ON TAN0201.INTERFACE_ID = TSU0220.INTERFACE_ID AND TSU0220.ROLE_ID = #{accRoleId}
INNER JOIN TAN0214 ON TAN0201.INTERFACE_ID = TAN0214.INTERFACE_ID AND TAN0214.USER_ID = #{userId}
WHERE 1 = 1
AND TAN0201.CHANNEL_ID = #{channelId}
AND TAN0201.INTERFACE_NM LIKE '%'||#{interfaceNm}||'%'
AND TAN0201.INTEGRATION_ID LIKE '%'||#{integrationId}||'%'
AND TAN0101.BUSINESS_ID = #{businessId}
) TAN0201 ON
(ID_MAP.GROUP_ID || '_' || ID_MAP.INTF_ID) = TAN0201.INTEGRATION_ID
ID_MAP.INTF_ID = TAN0201.INTEGRATION_ID
) INTF_INFO ON QA_MASTERLOG.MASTERLOG_ID = INTF_INFO.MASTERLOG_ID

SELECT TRACKING.RNUM , TRACKING.IDX , TRACKING.MSG_DATETIME , TRACKING.MASTERLOG_ID , TRACKING.DATA_SIZE , TRACKING.MSG_STATUS , TRACKING.ELAPSED_TIME , CASE WHEN TRACKING.MSG_STATUS = '00' THEN '완료' WHEN TRACKING.MSG_STATUS = '01' THEN '처리중' WHEN TRACKING.MSG_STATUS = '99' THEN '오류' ELSE '' END AS STATUS_NM
, TRACKING.GROUP_ID , TRACKING.INTF_ID , TRACKING.INTEGRATION_ID , TRACKING.INTERFACE_ID , TRACKING.INTERFACE_NM , TRACKING.MIN_PR_DT , TRACKING.MAX_PR_DT
, SND.SYSTEM_ID AS SND_SYSTEM_ID , SND.SYSTEM_CD AS SND_SYSTEM_CD , CASE WHEN SND.SYSTEM_NM IS NOT NULL THEN SND.SYSTEM_NM||'('||SND.SYSTEM_CD||')' ELSE '' END AS SND_SYSTEM_NM
, RCV.SYSTEM_ID AS RCV_SYSTEM_ID , RCV.SYSTEM_CD AS RCV_SYSTEM_CD , CASE WHEN RCV.SYSTEM_NM IS NOT NULL THEN RCV.SYSTEM_NM||'('||RCV.SYSTEM_CD||')' ELSE '' END AS RCV_SYSTEM_NM FROM ( SELECT RNUM , IDX , MSG_DATETIME , MASTERLOG_ID , DATA_SIZE , MSG_STATUS , ELAPSED_TIME
, GROUP_ID , INTF_ID
, INTEGRATION_ID , INTERFACE_ID , INTERFACE_NM
, MIN_PR_DT , MAX_PR_DT FROM ( SELECT ROWNUM AS RNUM , SUB_FROM_01.IDX , SUB_FROM_01.MSG_DATETIME , SUB_FROM_01.MASTERLOG_ID , SUB_FROM_01.DATA_SIZE , SUB_FROM_01.MSG_STATUS , SUB_FROM_01.ELAPSED_TIME
, SUB_FROM_01.GROUP_ID , SUB_FROM_01.INTF_ID
, SUB_FROM_01.INTEGRATION_ID , SUB_FROM_01.INTERFACE_ID , SUB_FROM_01.INTERFACE_NM , SUB_FROM_01.CHANNEL_ID , SUB_FROM_01.BUSINESS_ID
, SUB_FROM_01.MSG_DATETIME AS MIN_PR_DT , SUB_FROM_01.MSG_DATETIME AS MAX_PR_DT FROM ( SELECT QA_MASTERLOG.IDX , QA_MASTERLOG.MSG_DATETIME , QA_MASTERLOG.MASTERLOG_ID , QA_MASTERLOG.DATA_SIZE , QA_MASTERLOG.MSG_STATUS , QA_MASTERLOG.P_TIME AS ELAPSED_TIME
, INTF_INFO.GROUP_ID , INTF_INFO.INTF_ID
, INTF_INFO.INTEGRATION_ID , INTF_INFO.INTERFACE_ID , INTF_INFO.INTERFACE_NM , INTF_INFO.CHANNEL_ID , INTF_INFO.BUSINESS_ID
FROM ( SELECT /*+ INDEX_DESC (QA_MASTERLOG IX_QA_MASTERLOG_MSDT_01) */ ROWNUM AS IDX, QA_MASTERLOG.MSG_DATETIME, QA_MASTERLOG.MASTERLOG_ID, QA_MASTERLOG.MSG_STATUS, QA_MASTERLOG.DATA_SIZE, QA_MASTERLOG.P_TIME FROM QA_MASTERLOG QA_MASTERLOG WHERE 1 = 1 AND MSG_DATETIME BETWEEN #{startDate} AND #{endDate}
AND MSG_STATUS = #{status}
AND MSG_DATETIME = #{msgDateTime}
) QA_MASTERLOG
INNER JOIN ( SELECT DISTINCT ID_MAP.MASTERLOG_ID , ID_MAP.GROUP_ID , ID_MAP.INTF_ID
, NVL(TAN0201.INTEGRATION_ID, ID_MAP.INTF_ID) AS INTEGRATION_ID , NVL(TAN0201.INTERFACE_ID , ID_MAP.INTF_ID) AS INTERFACE_ID , NVL(TAN0201.INTERFACE_NM , '미등록 인터페이스') AS INTERFACE_NM , NVL(TAN0201.CHANNEL_ID , '미등록 인터페이스') AS CHANNEL_ID , NVL(TAN0201.BUSINESS_ID , '미등록 인터페이스') AS BUSINESS_ID
FROM QA_MASTERLOG_ID_MAP ID_MAP
LEFT OUTER JOIN
INNER JOIN
( SELECT DISTINCT TAN0201.INTEGRATION_ID , TAN0201.INTERFACE_ID , TAN0201.INTERFACE_NM , TAN0201.CHANNEL_ID , TAN0101.BUSINESS_ID FROM TAN0201 TAN0201 INNER JOIN TAN0101 ON TAN0101.INTERFACE_ID = TAN0201.INTERFACE_ID
INNER JOIN TAN0213 ON TAN0213.INTERFACE_ID = TAN0201.INTERFACE_ID INNER JOIN TIM0102 ON TAN0213.SYSTEM_ID = TIM0102.C_SYS_ID AND TIM0102.P_SYS_ID = #{systemId}
INNER JOIN TAN0213 ON TAN0213.INTERFACE_ID = TAN0201.INTERFACE_ID INNER JOIN TIM0403 ON TIM0403.SYSTEM_ID = TAN0213.SYSTEM_ID INNER JOIN TIM0402 ON TIM0402.C_ORG_ID = TIM0403.ORG_ID AND TIM0402.P_ORG_ID = #{organizationId} INNER JOIN TIM0102 ON TIM0102.P_SYS_ID = TIM0403.SYSTEM_ID
----------------------------------------------- -- 데이터 엑세스 권한 별 인터페이스 조회 ----------------------------------------------- INNER JOIN ( SELECT DISTINCT TSU0220.INTERFACE_ID FROM ----------------------------------------------- -- 로그인 사용자 데이터 엑세스 권한 조회(공통) ----------------------------------------------- ( SELECT DISTINCT TSU0219.USER_ID AS USER_ID, TSU0221.C_ROLE_ID AS ROLE_ID FROM TSU0219 INNER JOIN TSU0221 ON TSU0221.P_ROLE_ID = TSU0219.ROLE_ID WHERE TSU0219.USER_ID = #{userId} ) ACCESS_ROLE ----------------------------------------------- -- 데이터 엑세스 권한 :: 인터페이스 맵핑 조인 ----------------------------------------------- INNER JOIN TSU0220 TSU0220 ON TSU0220.ROLE_ID = ACCESS_ROLE.ROLE_ID
UNION
----------------------------------------------- -- 로그인 사용자 본인이 등록한 인터페이스 목록 조회 ----------------------------------------------- SELECT INTERFACE_ID FROM TAN0201 WHERE REG_USER = #{userId} ) ACCESS_ROLE_INTF ON TAN0201.INTERFACE_ID = ACCESS_ROLE_INTF.INTERFACE_ID
INNER JOIN TSU0220 ON TAN0201.INTERFACE_ID = TSU0220.INTERFACE_ID AND TSU0220.ROLE_ID = #{accRoleId}
INNER JOIN TAN0214 ON TAN0201.INTERFACE_ID = TAN0214.INTERFACE_ID AND TAN0214.USER_ID = #{userId}
WHERE 1 = 1
AND TAN0201.CHANNEL_ID = #{channelId}
AND TAN0201.INTERFACE_NM LIKE '%'||#{interfaceNm}||'%'
AND TAN0201.INTEGRATION_ID LIKE '%'||#{integrationId}||'%'
AND TAN0101.BUSINESS_ID = #{businessId}
) TAN0201 ON
(ID_MAP.GROUP_ID || '_' || ID_MAP.INTF_ID) = TAN0201.INTEGRATION_ID
ID_MAP.INTF_ID = TAN0201.INTEGRATION_ID
) INTF_INFO ON QA_MASTERLOG.MASTERLOG_ID = INTF_INFO.MASTERLOG_ID ) SUB_FROM_01 WHERE 1 = 1 AND ROWNUM <= #{endIndex} ) SUB_FROM_02 WHERE 1=1 AND RNUM >= #{startIndex} ) TRACKING
LEFT OUTER JOIN ( SELECT DISTINCT TIM0101.SYSTEM_NM , TAN0213.SYSTEM_ID , TIM0101.SYSTEM_CD , TAN0213.INTERFACE_ID , TAN0213.SEQ FROM TAN0213 TAN0213 INNER JOIN TIM0101 TIM0101 ON TAN0213.SYSTEM_ID = TIM0101.SYSTEM_ID AND TAN0213.NODE_TYPE = '0'
AND TAN0213.SYSTEM_ID = #{systemId}
AND TAN0213.SEQ = ( SELECT MAX(MAX0213.SEQ) FROM TAN0213 MAX0213 WHERE MAX0213.NODE_TYPE = '0' AND MAX0213.INTERFACE_ID = TAN0213.INTERFACE_ID )
) SND ON SND.INTERFACE_ID = TRACKING.INTERFACE_ID
LEFT OUTER JOIN ( SELECT DISTINCT TIM0101.SYSTEM_NM , TAN0213.SYSTEM_ID , TIM0101.SYSTEM_CD , TAN0213.INTERFACE_ID , TAN0213.SEQ FROM TAN0213 TAN0213 INNER JOIN TIM0101 TIM0101 ON TAN0213.SYSTEM_ID = TIM0101.SYSTEM_ID AND TAN0213.NODE_TYPE = '2'
AND TAN0213.SYSTEM_ID = #{systemId}
AND TAN0213.SEQ = ( SELECT MAX(MAX0213.SEQ) FROM TAN0213 MAX0213 WHERE MAX0213.NODE_TYPE = '2' AND MAX0213.INTERFACE_ID = TAN0213.INTERFACE_ID )
) RCV ON RCV.INTERFACE_ID = TRACKING.INTERFACE_ID
ORDER BY IDX

