mapper.select:
SELECT a.AID ,a.APP_TYPE ,TRT0207.APP_TYPE_NM AS APP_TYPE_NM ,a.NAME ,a.SEQ ,a.CD ,a.TYPE ,cd3.NM AS TYPE_NM ,a.TAG_YN ,a.INPUT_TYPE ,cd2.NM AS INPUT_TYPE_NM ,a.DEFAULT_VALUE ,a.REQUIRED_YN ,a.COMMENTS ,a.DEL_YN ,a.REG_DATE ,a.REG_USER ,a.MOD_DATE ,a.MOD_USER ,b.CD as BCD ,b.NM as BNM ,b.SEQ AS BSEQ ,b.COMMENTS AS BCOMMENTS ,b.DEL_YN AS BDEL_YN ,b.REG_DATE AS BREG_DATE ,b.REG_USER AS BREG_USER ,b.MOD_DATE AS BMOD_DATE ,b.MOD_USER AS BMOD_USER FROM (SELECT * FROM TRT0203 WHERE DEL_YN = 'N') a LEFT OUTER JOIN (SELECT * FROM TRT0204 WHERE DEL_YN = 'N') b ON a.AID = b.AID AND a.APP_TYPE = b.APP_TYPE LEFT OUTER JOIN TRT0207 TRT0207 ON TRT0207.APP_TYPE = a.APP_TYPE LEFT OUTER JOIN TSU0301 cd2 ON cd2.LEVEL1 = 'RT' AND cd2.LEVEL2 = '02' AND cd2.CD = a.INPUT_TYPE LEFT OUTER JOIN TSU0301 cd3 ON cd3.LEVEL1 = 'RT' AND cd3.LEVEL2 = '03' AND cd3.CD = a.TYPE
WHERE a.APP_TYPE = #{appType}
ORDER BY a.APP_TYPE , a.SEQ ASC , b.SEQ ASC

mapper.insert:

		select


	        COALESCE(max(AID::numeric), 0) + 1 as aid


			COALESCE(max(to_number(AID)), 0) + 1 as aid


