       	,#{aid,			jdbcType=VARCHAR}::numeric


		,to_number(#{aid,jdbcType=VARCHAR})


		,#{type,		jdbcType=VARCHAR}
		,#{tagYn,		jdbcType=VARCHAR}
		,#{inputType,	jdbcType=VARCHAR}
